import { Component, OnInit, Input } from '@angular/core';
import { AngularFirestore } from '@angular/fire/firestore';
import { MatDialog } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { ApiService } from 'src/app/service/api.service';


@Component({
  selector: 'app-produk',
  templateUrl: './produk.component.html',
  styleUrls: ['./produk.component.scss']
})
export class ProdukComponent implements OnInit {
  
  jenis = "Parent to Child";

  constructor(
    public router: Router,
    public api:ApiService,
    public firestore: AngularFirestore,
    public dialog: MatDialog
  ) { }

  
  ngOnInit(): void {
    this.getPlants();
  }

  jenisTnmCard = [
    {'title':'Tanaman hias akar', 'jenis':'akar', 'img':'https://firebasestorage.googleapis.com/v0/b/plantshop-2dc5b.appspot.com/o/PlantImg%2FIMG-20200601-WA0009.jpg?alt=media&token=0381be8f-9096-4e17-96d3-f296fc26e83a'},

    {'title':'Tanaman hias buah', 'jenis':'buah', 'img':'https://firebasestorage.googleapis.com/v0/b/plantshop-2dc5b.appspot.com/o/PlantImg%2FFB_IMG_16055231085890092.jpg?alt=media&token=6d9c3a6f-690e-4355-848a-5c2fd1900495'},

    {'title':'Tanaman hias bunga', 'jenis':'bunga', 'img':'data:image/jpeg;base64,'},

    {'title':'Tanaman hias daun', 'jenis':'daun', 'img':'data:image/jpeg;base64,'},

    {'title':'Tanaman hias pohon', 'jenis':'pohon', 'img':'data:image/jpeg;base64,'}
  ]
  
  plants: any = [];
  getPlants(){
    this.api.getDataPlants().subscribe(res=>{
      this.plants = res
    })
  }

  cekdata(data:any){
    this.api.sendPlants(data);
    this.router.navigate(['customer/produk']);
  }

  kirimJenis(data:any){
   this.api.sendJenis(data)
   this.jenis = data;
   this.router.navigate(['customer/jenis']);    
  }
}
